import Layout from '../components/Layout.js';
import fetch from 'isomorphic-unfetch';

import clientConfig from '../clientConfig';

import customeStyle from '../css/customStyle.module.css';

const Index = (props) => {
    console.log(props.products)
    return(
        <Layout>
            <ul>
                <p>All Products</p>

                {props.products.map( product => {
                    return <li key={product.name}>Product Name: {product.name} </li>
                })}
            </ul>

            <div className="customStyle.card">
                i am a card
            </div>
        </Layout>
    );
}

 Index.getInitialProps = async () => {
    const result = await fetch(`${clientConfig.siteURL}/products`);
    const prodData = await result.json();
    console.log(prodData);
    return {
        products: prodData
    }
 }

