import fetch from 'isomorphic-unfetch';
import { loadGetInitialProps } from 'next/dist/next-server/lib/utils';

const Products = (props) => {
    return(
        <div>
            <ul className="productShowcase">
                {props.products.map( product => {
                    return <Product product={product} key={product.name} />
                })}
            </ul>
        </div>
    );
}

const Product = (props) => {
    console.log(props.product);
    const prod = props.product;
    return(        
        <div className="carCard">
            <img  alt={prod.name} className="prodImage"/>
            <div className="carData">
                <p className="infoField carName">{prod.name}</p>
                <Price price={prod.price} salePrice={prod.sale_price}/>
                <p className="infoField">Växel</p>
                <p className="infoField">Bränsle</p>
                <p className="infoField">Årsmodell</p>
                <p className="infoField">Miltal</p>
                <p className="infoField">Hästkraft</p>
            </div>
        </div>
    );
}

const Price = (props) => {
    if(!props.salePrice && props.salePrice > 0 ) {
        return(
            <p className="infoField carPrice">{props.price} sek</p>
        );
    }
    return(
        <p className="salePriceDisplayer">
            <span className="oldPrice">{props.price}</span>
            <span className="discountedPrice">{props.salePrice} sek</span>
        </p>
    );
}

export default Products;
