import fetch from 'isomorphic-unfetch';
import { loadGetInitialProps } from 'next/dist/next-server/lib/utils';

const Products = (props) => {
    console.log(props.products[0]);
    return(
        <div>
            <ul className="productShowcase">
                {props.products.map( product => {
                    return <Product product={product} key={product.name} />
                })}
            </ul>
        </div>
    );
}

const Product = (props) => {
    //console.log(props.product);
    const prod = props.product;
    const gearBoxAttr = prod.attributes.filter( p => p.name.toLowerCase() == 'växellåda');
    const gearBox = gearBoxAttr[0].options;
    console.log(gearBox);
    return(        
        <div className="carCard">
            <img src={prod.images[0].src} alt={prod.name} className="prodImage"/>
            <div className="carData">
                <p className="infoField carName">{prod.name}</p>
                <Price 
                    price={prod.price} 
                    salePrice={prod.sale_price} 
                    regularPrice={prod.regular_price}/>
                <p className="infoField">
                    <span>Växellåda</span>
                    <span className="attributeValue">
                        { gearBox|| ''}
                    </span>
                </p>
                <p className="infoField">
                    <span>Bränsle</span>
                </p>
                <p className="infoField">
                    <span>Årsmodell</span>
                </p>
                <p className="infoField">
                    <span>Miltal</span>
                </p>
                <p className="infoField">
                    <span>Hästkraft</span>
                </p>
            </div>
        </div>
    );
}

const Price = (props) => {
    if(props.salePrice) {
        console.log("salePrice is " + props.salePrice + " and price is " + props.regularPrice);
        return(
            <p className="salePriceDisplayer">
                <span className="oldPrice">{props.regularPrice}</span>
                <span className="discountedPrice">{props.salePrice} sek</span>
            </p>
        );
    }
    return(
        <p className="infoField carPrice">{props.price} sek</p>
    );
}

export default Products;
