import Layout from '../components/Layout';

import fetch from 'isomorphic-unfetch'

import {url} from '../util/woocommerce';

import {withRouter} from 'next/router';
const Product = (props) => {
    console.log(url);
    return(
        <Layout>
            Implementing the product page
            <main>
                <h1>{url}</h1>
                <h1>{props.router.pathname}</h1>
                <h1>{Object.entries(props.router.query)}</h1>
            </main>
        </Layout>
    );
}

Product.getInitialProps = async ctxt => {
    let {query} = ctxt;
    const id = query.id;
    console.log(query.slug);
    const raw = await fetch(`${url}product/${query.slug}`);
    const prodData = await raw.json();
    return {
        product: prodData
    }
}



export default withRouter(Product);
