import Layout from '../components/Layout';
import siteUrl from '../clientConfig';

import fetch from 'isomorphic-unfetch';

import {withRouter} from 'next/router';
const Product = (props) => {
    return(
        <Layout>
            Implementing the product page
            <main>
                <h1>{props.router.pathname}</h1>
                <h1>{Object.entries(props.router.query)}</h1>
            </main>
        </Layout>
    );
}

Product.getInitialProps = async ctxt => {
    console.log(ctxt.query.id);
    const prodRaw = await fetch(`${siteUrl}/products/${ctxt.query.id}`);
    console.log('Product Raw data ' + prodRaw);
    const prodData = await prodRaw.json();

    return{
        product: prodData
    }
}

export default withRouter(Product);
