import Layout from '../components/Layout';
import clientConfig from '../clientConfig';

import fetch from 'isomorphic-unfetch';

import {withRouter} from 'next/router';
const Product = (props) => {
    return(
        <Layout>
            Implementing the product page
            <main>
                <h1>{clientConfig.siteURL}</h1>
                <h1>{props.router.pathname}</h1>
                <h1>{Object.entries(props.router.query)}</h1>
            </main>
        </Layout>
    );
}

Product.getInitialProps = async ctxt => {
    const raw = await fetch(`${clientConfig.siteURL}/products/3069`);
    console.log( raw );
    return {
        product: raw
    }
}



export default withRouter(Product);
